import "bootstrap/dist/css/bootstrap.min.css"
import { useState } from "react";

function App() {
    const [userName, setUserName] = useState("");
    const [userAge, setUserAge] = useState("");
    const [userAddr, setUserAddr] = useState("");
    // 웹에서 입력되는 정보가 변수로서 변화를 허용하기 위해 useState() 사용

    const [userArray, setUserArray] = useState([]);
    // 입력되어 저장된 정보를 배열로 만들어주기 위해 변수 선언

    function handleUserName(event) {
        console.log("함수 들어왔는지 확인하려고 쓰는 console.log()")
        console.log(event.target.value);
        // 이름 입력창에 쓰면서 콘솔을 확인해보면 실시간으로 입력되는 정보를 콘솔에서 확인할 수 있다.
        setUserName(event.target.value);
    }
    function handleUserAge(event) {
        console.log("함수 들어왔는지 확인하려고 쓰는 console.log()")
        console.log(event.target.value);
        // 이름 입력창에 쓰면서 콘솔을 확인해보면 실시간으로 입력되는 정보를 콘솔에서 확인할 수 있다.
        setUserAge(event.target.value);
    }
    function handleUserAddr(event) {
        console.log("함수 들어왔는지 확인하려고 쓰는 console.log()")
        console.log(event.target.value);
        // 이름 입력창에 쓰면서 콘솔을 확인해보면 실시간으로 입력되는 정보를 콘솔에서 확인할 수 있다.
        setUserAddr(event.target.value);
    }
    function onSubmit(event) {
        event.preventDefault();
        const tempObject = {
            name: userName,
            age: userAge,
            addr: userAddr
        }
        console.log(tempObject);
        // setUserArray(userArray.concat(tempObject));
        // // setUserArray(userArray를.concat합치겠다(tempObject를 이용할 것이다));
        setUserArray((prevValue) => [...prevValue, tempObject]);
        // spread: [... x] : x가 배열이고, 그 배열을 풀어헤친다는 의미.
        // prevValue = [ {..} , {..}, .. , {..} ]
        // 근데 배열 안에 객체가 있기 때문에 ...prevValue = {..}, {..}, .. , {..}
        // tempObject = {..}
        // [...prevValue, tempObject] = [ {a}, {b}, {c}, ... ,{tempObject} ]
        // spread는 기존 배열을 건드리지 않은 채로 새로운 배열을 만들어낸다. 불변성을 지키는!

        console.log(userArray);
        // 값이 "객체" 형태로 들어가는 것을 확인할 수 있당.
    }

    return (
        <div className = "container mt-3 text-center">
            <h1> 회원 정보 관리 앱 😬 </h1>
            <hr />

            <form>
                <input onChange = {handleUserName} className = "form-control mb-2" type = "text" placeholder = "이름 입력" />
                <input onChange = {handleUserAge} className = "form-control mb-2" type = "text" placeholder = "나이 입력" />
                <input onChange = {handleUserAddr} className = "form-control mb-2" type = "text" placeholder = "주소 입력" />
                {/* 입력되는 정보를 받아내기 위해
                    1. input onchange = {}                   인풋에 Onchange 이벤트를 넣어준다.
                    2. onchange = {handleUserName}           onchange 이벤트의 함수 이름을 지정해준다.
                    3. function handleUserNamer(event) {...} 입력되는 정보를 받아오긴 위한 함수를 상위에서 생성해준다. */}
                <button onClick = {onSubmit} className = "btn btn-primary">등록하기</button>
                {/* 정보 입력 후 버튼을 눌렀을 때 가입된 회원 목록으로 정보를 넘기기 위해
                    1. input onClick = {}               버튼을 눌러서 입력된 정보를 저장하기 위해 이벤트를 넣어준다.
                    2. onClick = {onSubmit}             onSubmit 함수가 적용되게 한다.
                    3. function onSubmit() { ... }      함수는 상위에 생성했다. */}
            </form>
            <hr />
            <h1>가입된 회원 목록 🫶🏻 </h1>
            {userArray.length > 1 ?
                <table className = "table table-dark table-striped">
                    <thead>
                        <tr>
                            <th>이름</th>
                            <th>나이</th>
                            <th>주소</th>
                        </tr>
                    </thead>

                    <tbody>
                        {userArray.map((item, index) => {
                            return ( 
                                <tr key = {index}>
                                    <td>{item.name}</td>
                                    <td>{item.age}</td>
                                    <td>{item.addr}</td>
                                </tr>
                                )
                        })}
                    </tbody>
                </table> : <p className= "text-dange fw-bold"> 데이터가 없습니다. 입력해주세요.</p>
            }
        </div>
    );
}

export default App;
