import "bootstrap/dist/css/bootstrap.min.css"
import sampleData from "./sampleData.json"
import Path from "./Path.js"

function App() {

    const subject = "React";
    const sampleTag = <h3> 데이터 바인딩 연습중</h3>;
    const fruits = ["apple", "pineapple", 'banana', 'kiwi', 'mango']
    const girlsData = sampleData;

    console.log(girlsData);

    return (
        <div className = "container text-center">
            <h1>{subject} 공부를 시작합시다.</h1>
            {sampleTag}

            {fruits.map((item, index) => {
              return (<p key = {index}>{index+1}번째 과일은 {item}입니다.</p>
              )
              // 반복변수
            })}
            <hr />
            {/* 맵 함수를 이용해서 Child 컴포넌트 반복 및 재사용 코드 작성 */}
            {girlsData.map((item, index) => {
                return (
                    <Path key = {index} path = {item.path} name = {item.name} />
                )
            })}
            <hr />

        </div>
        // <div></div> 로 감싸주거나 그냥 태그를 이용하거나
        // <React.Fragment></React.Fragment>로도 감싸줄 수 있는데 이건 잘 안쓴다. 비용문제도 있음.
    );
}

export default App;
