import "bootstrap/dist/css/bootstrap.min.css"
import { useState } from "react";

function App() {
{/* 리액트에서는 모든 변수가 불변성을 지니고 있다.
변하게 만들어주려면 Hooks를 사용해야한다. 훅스는 가장 상단에 써준다.
UseState는 const로 선언한다 */}
    const [count, setCount] = useState(0);
    // 1번째 인자: 변수, 2번째 인자: 변수 값을 변화시킬 함수
    // 2번때 인자의 가장 앞에는 set 키워드를 붙여서 사용!
    // useState(0) : 0은 초기값을 나타낸다.
    const [count2, setCount2] = useState(1);

    function increase() {
        setCount(count + 10);
        // setCount((이전값) => 이전값+1); // 가능..
    }

    function decrease() {
        setCount(count - 1);
        // setCount((prevValue) => prevValue-1); // 가능..
    }

    function multiple() {
        setCount((prevValue) => prevValue*2);
    }

    return (
        <div className = "container text-center mt-3">
            <h1>React Hooks</h1>
            <hr />
            <h4>Current Value is : {count}</h4>

            <button className = "btn btn-primary me-2"
                    onClick={increase}>
                Increase + 10
            </button>

            <button className = "btn btn-primary"
                    onClick={decrease}>
                Decrease
            </button>

            <button className = "btn btn-primary mi-2"
                    onClick={multiple}>
                Multiple * 2
            </button>

        </div>

    );
}

export default App;
