import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css"
import { useState, useRef, useEffect } from "react";

function App() {
    const [userName, setUserName] = useState("");
    const [userAge, setUserAge] = useState("");
    const [userAddr, setUserAddr] = useState("");
    // 웹에서 입력되는 정보가 변수로서 변화를 허용하기 위해 useState() 사용

    const [userArray, setUserArray] = useState([]);
    // 입력되어 저장된 정보를 배열로 만들어주기 위해 변수 선언

    const ref = useRef();

    // 1. fetch() -> 기본 JavaScript 내장 라이브러리
        // .then() -> 응답이 성공적일 때, 매개변수에는 성공응답에 대한 함수를 써준다.
            // 예: (응답저장변수 => 응답저장변수를.json으로 바꿔줘요)
            // 예: (응답저장변수 => 콘솔로그에서(응답저장변수) 보여줘요)
        // .catch() ->  응답이 실패일 때
        // 두 경우를 다 가지고 오는 함수
    // 2. axios -> Node.js 기반 외부 패키지 (설치 필요)

    useEffect(() => {
        // function getInformation() {
        //     fetch("http://localhost:4000/information")
        //         .then(res => res.json())
        //         .then(res => console.log(res))
        //         .catch(err => console.log(err))
        // }
        // getInformation();
        async function getInformation() {
            try {
                const result = await axios.get("http://localhost:4000/information");
                setUserArray(result.data)
                // UserArray를 불러와야 입력된 데이터들이 새로고침을 해도 화면에 계속 보인당.
            } catch (error) {
                console.lost(error)
            }
        }
        getInformation();
    }, []);


    function handleUserName(event) {
        console.log("함수 들어왔는지 확인하려고 쓰는 console.log()")
        console.log(event.target.value);
        // 이름 입력창에 쓰면서 콘솔을 확인해보면 실시간으로 입력되는 정보를 콘솔에서 확인할 수 있다.
        setUserName(event.target.value);
    }
    function handleUserAge(event) {
        console.log("함수 들어왔는지 확인하려고 쓰는 console.log()")
        console.log(event.target.value);
        // 이름 입력창에 쓰면서 콘솔을 확인해보면 실시간으로 입력되는 정보를 콘솔에서 확인할 수 있다.
        setUserAge(event.target.value);
    }
    function handleUserAddr(event) {
        console.log("함수 들어왔는지 확인하려고 쓰는 console.log()")
        console.log(event.target.value);
        // 이름 입력창에 쓰면서 콘솔을 확인해보면 실시간으로 입력되는 정보를 콘솔에서 확인할 수 있다.
        setUserAddr(event.target.value);
    }

    async function onSubmit(event) {
        event.preventDefault();
        if (userName === "" || userAge === "" || userAddr === "") {
            alert("입력이 다 되었는지 확인하고 다시 입력해주세요.");
        } else {
            const tempId = new Date()
            const tempObject = {
                id: tempId.getTime(),
                name: userName,
                age: userAge,
                addr: userAddr
            }
            await axios.post("http://localhost:4000/information", tempObject);
            // console.log(tempObject);
            // setUserArray(userArray.concat(tempObject));
            // // setUserArray(userArray를.concat합치겠다(tempObject를 이용할 것이다));
            setUserArray((prevValue) => [...prevValue, tempObject]);
            // spread: [... x] : x가 배열이고, 그 배열을 풀어헤친다는 의미.
            // prevValue = [ {..} , {..}, .. , {..} ]
            // 근데 배열 안에 객체가 있기 때문에 ...prevValue = {..}, {..}, .. , {..}
            // tempObject = {..}
            // [...prevValue, tempObject] = [ {a}, {b}, {c}, ... ,{tempObject} ]
            // spread는 기존 배열을 건드리지 않은 채로 새로운 배열을 만들어낸다. 불변성을 지키는!
            setUserName("");
            setUserAge("");
            setUserAddr("");
            // 입력된 정보를 받아 새로운 배열을 만든 후 입력칸을 초기화시켜준다.
            ref.current.focus();
            // 입력이 끝나면 자동으로 입력 커서가 지정된 곳으로 돌아가게끔 초기화시키는 함수다.
            // 회원 정보 form에서 이름 input 칸에 ref = {ref}로 설정해주므로써 입력이 끝나고 정보를 받고 난 후에 커서가 이름칸으로 자동으로 간다.

            //console.log(userArray);
            // 값이 "객체" 형태로 들어가는 것을 확인할 수 있당.
        }
    }

    async function onDelete(deletedId) {
        // console.log(id);
        // axios.delete("http://localhost:4000/information/" + id); 이렇게 써도 되고
        await axios.delete(`http://localhost:4000/information/${deletedId}`);
        /* 프론트 상에서 삭제가 처리된 후의 데이터를 표시하기 위해서 useArray를 사용한다.
        그 이유는 그동안 데이터를 웹에 표시하기 위해 useArray를 사용했기 때문에 여기서도..
        데이터를 삭제함에 있어서 split는 사용하면 안되용. filter를 써줘용. 
        e.g., test.filter((item) => item == 2) : test 배열에서 item이 2인 것으로만 filtering 해서 뽑아줘용. */
        setUserArray(userArray.filter(item => item.id != deleteId));
    }

    return (
        <div className = "container mt-3 text-center">
            <h1> 회원 정보 관리 앱 😬 </h1>
            <hr />

            <form>
                <input ref = {ref} onChange = {handleUserName} value = {userName} className = "form-control mb-2" type = "text" placeholder = "이름 입력" />
                <input onChange = {handleUserAge} value = {userAge} className = "form-control mb-2" type = "text" placeholder = "나이 입력" />
                <input onChange = {handleUserAddr} value = {userAddr} className = "form-control mb-2" type = "text" placeholder = "주소 입력" />
                {/* 입력되는 정보를 받아내기 위해
                    1. input onchange = {}                   인풋에 Onchange 이벤트를 넣어준다.
                    2. onchange = {handleUserName}           onchange 이벤트의 함수 이름을 지정해준다.
                    3. function handleUserNamer(event) {...} 입력되는 정보를 받아오긴 위한 함수를 상위에서 생성해준다. */}
                <button onClick = {onSubmit} className = "btn btn-primary">등록하기</button>
                {/* 정보 입력 후 버튼을 눌렀을 때 가입된 회원 목록으로 정보를 넘기기 위해
                    1. input onClick = {}               버튼을 눌러서 입력된 정보를 저장하기 위해 이벤트를 넣어준다.
                    2. onClick = {onSubmit}             onSubmit 함수가 적용되게 한다.
                    3. function onSubmit() { ... }      함수는 상위에 생성했다. */}
            </form>
            <hr />
            <h1>가입된 회원 목록 🫶🏻 </h1>
            {userArray.length > 0 ?
                <table className = "table table-dark table-striped">
                    <thead>
                        <tr>
                            <th>이름</th>
                            <th>나이</th>
                            <th>주소</th>
                            <th>수정</th>
                            <th>삭제</th>
                        </tr>
                    </thead>

                    <tbody>
                        {userArray.map((item, index) => {
                            return ( 
                                <tr key = {index}>
                                    <td>{item.name}</td>
                                    <td>{item.age}</td>
                                    <td>{item.addr}</td>
                                    <td><button className = "btn btn-success">수정</button></td>
                                    <td><button onClick = {() => onDelete(item.id)} className = "btn btn-danger">삭제</button></td>
                                    {/* 버튼에는 btn btn-... 으로 버튼 특성마다의 색의 닉네임이 붙는다.
                                    primary 파란색 secondary 회색 success 초록색 danger 빨간색.. */}
                                </tr>
                            )
                        })}
                    </tbody>
                </table> : <p className= "text-danger fw-bold"> 데이터가 없습니다. 입력해주세요.</p>
            }
        </div>
    );
}

export default App;
